execution.script:
pipeline {
    agent any
    parameters {
        choice(name: 'skip_test', choices: ['skip_test', 'Do_not'], description: 'Select the environment')
    }
    tools {
         git 'Default'
         jdk 'java'
        maven 'maven'
    }
    stages {
        stage('Checkout') {
            steps {
              checkout scmGit(branches: [[name: '*/master'], [name: '*/ninja']], extensions: [], userRemoteConfigs: [[url: 'https://github.com/opstree/spring3hibernate.git']])
            }
        }
        stage('Build') {
            steps {
                sh '''mvn clean package'''
                // Add commands to build application
            }
        }

        stage('Test') {
            //when { expression { params.skip_test != true } }
            when { expression { params.skip_test == 'Do_not' } }
            parallel {
                stage('Code Stability Tests') {

                    steps {
                        sh 'echo "Running unit tests"'
                        sh 'mvn surefire:test'

                        // Add commands to run unit tests
                    }
                }
                stage('Code Quality Analysis Tests') {
                    steps {
                        sh 'echo "Running unit tests"'
                        sh 'mvn checkstyle:checkstyle'
                        recordIssues(tools: [checkStyle(pattern: '**/checkstyle-result.xml')])
                        // Add commands to run unit tests
                    }
                }
                stage('Code Coverage Tests') {
                    steps {
                        sh 'echo "Running integration tests"'
                        sh 'mvn cobertura:cobertura'
                        recordIssues(tools: [findBugs(pattern: '**/findbugsXml.xml', useRankAsPriority: true)])
                        // Add commands to run integration tests
                    }
                }
            }
        }

       stage('Report Generation ') {
            steps {
                echo 'Generating Report'
                sh 'mvn checkstyle:checkstyle  >>  /var/lib/jenkins/report_checkstyle.html'
                sh 'mvn cobertura:cobertura  >> /var/lib/jenkins/report_cobertura.html'
            }
        }
        stage('user input'){
            input {
                message 'Do you want to fail the pipeline'
                ok 'No'
                submitter 'ritesh'
               }
            steps {
                echo "end"
                }
            }
        stage('Artifact archieve') {
            steps {
             archiveArtifacts artifacts: 'archiveArtifacts artifacts: \'target/*.jar, target/*.war', followSymlinks: false


            }
        }

        stage('Deploy') {
            steps {
                sh 'echo "Deploying the application"'
                // Add commands to deploy application
            }

        }
    }
    post {
        success {
            echo "${env.JOB_NAME}"
               slackSend channel: 'jenkins_report', color: '#439FE0', message: "JOB_NAME: ${env.JOB_NAME} BUILD_ID: ${env.BUILD_ID} WORKSPACE: ${env.WORKSPACE} Successful", teamDomain: 'opstree', tokenCredentialId: 'slack-tokenn', username: 'Ritesh Kumar'
                mail to: 'riteshkumar19325@gmail.com',
     subject: "Job '${env.JOB_NAME}' (${env.BUILD_NUMBER}) is waiting for input",
     body: "Please go to ${env.BUILD_URL}."

               //emailext attachLog: true, body: '', recipientProviders: [contributor(), developers(), buildUser()], subject: '$BUILD_ID $JOB_NAME', to: 'riteshkumar19325@gmail.com'
               //emailext attachLog: true, body: '', recipientProviders: [contributor(), developers(), buildUser()], subject: '', to: 'riteshkumar19325@gmail.com'
               //emailext attachLog: true, body: '', recipientProviders: [previous(), buildUser(), contributor(), culprits(), brokenBuildSuspects()], subject: '$BUILD_ID', to: 'Extended E-mail Notification'
                  }
        failure {
            echo "${env.JOB_NAME}"
            slackSend channel: 'jenkins_report', color: '#439FE0', message: "JOB_NAME: ${env.JOB_NAME} BUILD_ID: ${env.BUILD_ID} WORKSPACE: ${env.WORKSPACE} Un-Successful", teamDomain: 'opstree', tokenCredentialId: 'slack-tokenn', username: 'Ritesh Kumar'
             }
        }
}
