    tools {
         git 'Default'
         jdk 'java'
        maven 'maven'
    }
    stages {
        stage('Checkout') {
            steps {
              checkout scmGit(branches: [[name: '*/master'], [name: '*/ninja']], extensions: [], userRemoteConfigs: [[url: 'https://github.com/opstree/spring3hibernate.git']])
            }
        }
        stage('Build') {
            steps {
                sh '''mvn clean package'''
                // Add commands to build application
            }
        }
         stage('user input'){
            input {
                message 'Do you want to fail the pipeline'
                ok 'No'
                submitter 'ritesh'
               }
            steps {
                echo "end"
                }
            }
        stage('Test') {
            when { expression { params.skip_test != true } }
            parallel {
                stage('Code Stability Tests') {

                    steps {
                        sh 'echo "Running unit tests"'
                        sh 'mvn surefire:test'

                        // Add commands to run unit tests
                    }
                }
                stage('Code Quality Analysis Tests') {
                    steps {
                        sh 'echo "Running unit tests"'
                        sh 'mvn checkstyle:checkstyle'
                        recordIssues(tools: [checkStyle(pattern: '**/checkstyle-result.xml')])
                        // Add commands to run unit tests
                    }
                }
                stage('Code Coverage Tests') {
                    steps {
                        sh 'echo "Running integration tests"'
                        sh 'mvn cobertura:cobertura'
                        // Add commands to run integration tests
                    }
                }
            }
        }

       stage('Report Generation ') {
            steps {
                echo 'Generating Report'
                sh 'mvn checkstyle:checkstyle  >>  /var/lib/jenkins/report_checkstyle.html'
                sh 'mvn cobertura:cobertura  >> /var/lib/jenkins/report_cobertura.html'
            }
        }
        stage('Artifact archieve') {
            steps {
             archiveArtifacts artifacts: 'archiveArtifacts artifacts: \'target/*.jar, target/*.war', followSymlinks: false


            }
        }
        stage('Slack Notification') {
            steps {

              slackSend channel: 'jenkins_report', color: '#439FE0', message: 'Build Number: ${BUILD_NUMBER}\nJob Name: ${JOB_NAME} \nStatus: successful', teamDomain: 'opstree', tokenCredentialId: 'slack-tokenn', username: 'Ritesh Kumar'

            }
        }
        stage('Email Notification') {
